INSERT INTO `qs_jobfair` VALUES ('188','8','1','1','1','1','0','苏州吴江区人力资源市场2014.2.14公益招聘会','#f00','1','综合','<p class=\"MsoNormal\" align=\"center\" style=\"margin-top:3.75pt;margin-right:0cm;margin-bottom:3.75pt;margin-left:0cm;text-align:center;mso-line-height-alt:13.5pt;mso-pagination:widow-orphan;\"><b><span style=\"font-size:16pt;font-family:宋体;\">吴江区<span lang=\"EN-US\">2014</span>年</span></b><span lang=\"EN-US\" style=\"font-size:9pt;font-family:Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"margin-top:3.75pt;margin-right:0cm;margin-bottom:3.75pt;margin-left:0cm;text-align:center;mso-line-height-alt:13.5pt;mso-pagination:widow-orphan;\"><b><span style=\"font-size:16pt;font-family:宋体;\">吴江区人力资源市场冬季公益招聘会</span></b><span lang=\"EN-US\" style=\"font-size:9pt;font-family:Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"center\" style=\"margin-top:3.75pt;margin-right:0cm;margin-bottom:3.75pt;margin-left:0cm;text-align:center;mso-line-height-alt:13.5pt;mso-pagination:widow-orphan;\"><b><span style=\"font-size:16pt;font-family:宋体;\">通知</span></b><span lang=\"EN-US\" style=\"font-size:9pt;font-family:Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin:3.75pt 0cm;line-height:13.5pt;\"><b><span style=\"font-size:12pt;font-family:宋体;\">各有关单位：</span></b><span lang=\"EN-US\" style=\"font-size:9pt;font-family:Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin:3.75pt 0cm;line-height:13.5pt;\"><span lang=\"EN-US\" style=\"font-size:12pt;font-family:宋体;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:12pt;font-family:宋体;\">为深入贯彻落实党的十八届三中全会精神，充分发挥各地各人力资源市场在促进就业中的主导作用，进一步做好<span lang=\"EN-US\">2014</span>年各项就业工作。根据人力资源和社会保障部办公厅《关于举办<span lang=\"EN-US\">2013</span>年全国人力资源市场高校毕业生就业服务周活动的通知》（人社厅发【<span lang=\"EN-US\">2013</span>】<span lang=\"EN-US\">104</span>号）要求，我区将定期举办公益招聘会，为吴江市民求职招聘搭建桥梁。</span><span lang=\"EN-US\" style=\"font-size:9pt;font-family:Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin:3.75pt 0cm;text-indent:24pt;line-height:13.5pt;\"><span style=\"font-size:12pt;font-family:宋体;\">苏州市吴江区是临沪临浙重要的制造业基地，人力资源服务需求强劲。一直以来，区人力资源市场秉承“服务民生、服务企业、服务发展”的宗旨，取得显著成绩。<span lang=\"EN-US\">2012</span>年，吴江区人力资源市场被江苏省人社厅评定为“江苏省五星级公共人力资源市场”。</span><span lang=\"EN-US\" style=\"font-size:9pt;font-family:Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin:3.75pt 0cm;text-indent:24pt;line-height:13.5pt;\"><span style=\"font-size:12pt;font-family:宋体;\">吴江区人力资源市场布局合理，设施齐全，功能完备。作为其载体的吴江区人力资源和社会保障服务大厦，总投资<span lang=\"EN-US\">3</span>个亿，总面积达<span lang=\"EN-US\">3</span>万平米。区人力资源市场于<span lang=\"EN-US\">2012</span>年<span lang=\"EN-US\">9</span>月正式建成投入使用，借鉴国内外多家人力资源市场的建设经验，专业规划，摊位宽敞。商务中心、网络控制、信息查询、技能测评等区域一应具有。市场招聘大厅达<span lang=\"EN-US\">2270</span>平米，档案室<span lang=\"EN-US\">80</span>平米，拥有室内室外二块大型<span lang=\"EN-US\">LED</span>电子显示屏，人数计数器、自助招聘摊位<span lang=\"EN-US\">12</span>个<span lang=\"EN-US\">,</span>常设招聘摊位<span lang=\"EN-US\">64</span>个，以及网络招聘室、视频招聘室、自助招聘摊位、面谈式招聘摊位等。实现了自助式求职招聘、摊位式洽谈招聘、网络招聘和视频招聘等功能。现将本次招聘会有关事项通知如下。</span><span lang=\"EN-US\" style=\"font-size:9pt;font-family:Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin:3.75pt 0cm;line-height:13.5pt;\"><b><span style=\"font-size:12pt;font-family:宋体;\">一、主办、承办单位</span></b><span lang=\"EN-US\" style=\"font-size:9pt;font-family:Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin:3.75pt 0cm;line-height:13.5pt;\"><span style=\"font-size:12pt;font-family:宋体;\">主办：吴江区人力资源和社会保障局</span><span lang=\"EN-US\" style=\"font-size:9pt;font-family:Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin:3.75pt 0cm;line-height:13.5pt;\"><span style=\"font-size:12pt;font-family:宋体;\">承办：吴江区就管中心、吴江区人力资源市场</span><span lang=\"EN-US\" style=\"font-size:9pt;font-family:Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin:3.75pt 0cm;line-height:13.5pt;\"><b><span style=\"font-size:12pt;font-family:宋体;\">二、时间、地点</span></b><span lang=\"EN-US\" style=\"font-size:9pt;font-family:Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin:3.75pt 0cm;line-height:13.5pt;\"><span style=\"font-size:12pt;font-family:宋体;\">时间：<span lang=\"EN-US\">2</span>月<span lang=\"EN-US\">14</span>日（星期五<span lang=\"EN-US\">8</span>：<span lang=\"EN-US\">30</span>―<span lang=\"EN-US\">12</span>：<span lang=\"EN-US\">00</span>）</span><span lang=\"EN-US\" style=\"font-size:9pt;font-family:Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin:3.75pt 0cm;line-height:13.5pt;\"><span style=\"font-size:12pt;font-family:宋体;\">地点：吴江区人力资源市场（长安路<span lang=\"EN-US\">3223</span>号，人社大厦一楼）</span><span lang=\"EN-US\" style=\"font-size:9pt;font-family:Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin:3.75pt 0cm;line-height:13.5pt;\"><b><span style=\"font-size:12pt;font-family:宋体;\">三、有关要求</span></b><span lang=\"EN-US\" style=\"font-size:9pt;font-family:Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin:3.75pt 0cm;line-height:13.5pt;\"><span lang=\"EN-US\" style=\"font-size:12pt;font-family:宋体;\">1</span><span style=\"font-size:12pt;font-family:宋体;\">、本次招聘会预设展位<span lang=\"EN-US\">64</span>个，用人单位免费进场，高校毕业生及市内相关求职者均可免费进场应聘求职。</span><span lang=\"EN-US\" style=\"font-size:9pt;font-family:Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin:3.75pt 0cm;line-height:13.5pt;\"><span lang=\"EN-US\" style=\"font-size:12pt;font-family:宋体;\">2</span><span style=\"font-size:12pt;font-family:宋体;\">、请用人单位请根据需要制定招聘计划。</span><span lang=\"EN-US\" style=\"font-size:9pt;font-family:Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin:3.75pt 0cm;line-height:13.5pt;\"><span lang=\"EN-US\" style=\"font-size:12pt;font-family:宋体;\">3</span><span style=\"font-size:12pt;font-family:宋体;\">、请参加招聘的用人单位持营业执照副本、经办人身份证复印件及招聘信息电子版到吴江区人力资源市场（长安路<span lang=\"EN-US\">3223</span>号人社大厦一楼）报名或将营业执照副本、经办人身份证复印件及招聘信息电子版传真邮箱。</span><span lang=\"EN-US\" style=\"font-size:9pt;font-family:Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin:3.75pt 0cm;line-height:13.5pt;\"><span lang=\"EN-US\" style=\"font-size:12pt;font-family:宋体;\">4</span><span style=\"font-size:12pt;font-family:宋体;\">、请用人单位自行制作易拉宝、<span lang=\"EN-US\">X</span>展架等宣传海报。</span><span lang=\"EN-US\" style=\"font-size:9pt;font-family:Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin:3.75pt 0cm;line-height:13.5pt;\"><b><span style=\"font-size:12pt;font-family:宋体;\">四、联系方式</span></b><span lang=\"EN-US\" style=\"font-size:9pt;font-family:Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin:3.75pt 0cm;line-height:13.5pt;\"><span style=\"font-size:12pt;font-family:宋体;\">联系人：周经理</span><span lang=\"EN-US\" style=\"font-size:9pt;font-family:Tahoma, sans-serif;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin:3.75pt 0cm;line-height:13.5pt;\"><span style=\"font-size:12pt;font-family:宋体;\">联系电话：<span lang=\"EN-US\">0512</span>―<span lang=\"EN-US\">88989800</span>、<span lang=\"EN-US\">88989789<o:p></o:p></span></span></p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin:3.75pt 0cm;line-height:13.5pt;\"><span style=\"font-size:12pt;font-family:宋体;\">电子邮箱：<span lang=\"EN-US\"><a href=\"mailto:799022456@qq.com\"><span style=\"font-size:10.5pt;mso-bidi-font-size:11.0pt;font-family:\" calibri\",\"sans-serif\";mso-ascii-theme-font:minor-latin;mso-fareast-font-fam